var item = {
  "fullname": "LE VAN THAO"
};
cy.request({
  method: 'PUT',
  url: '/users/5eedb6ec02fa8821a288bf7d',
  body: item,
  failOnStatusCode: false
}).its('status').should('eq', 401);
cy.request('/users/5eedb6ec02fa8821a288bf7d').then(function (response) {
  expect(response).to.have.property('status', 200);
  expect(response.body).to.not.be["null"];
  expect(response.body.user).to.have.haveOwnProperty('fullname', 'LE VAN THAO');
});

var item = {
  "email": "yourlap@gmail.com",
  "password": "yourlap@2020"
};
cy.request({
  method: 'POST',
  url: '/login',
  body: item,
  failOnStatusCode: false
}).then(function (response) {
  expect(response).to.have.property('status', 200);
  expect(response.body).to.not.be["null"];
  expect(response.body).to.have.property('message').eql('login successfully');
  expect(response.body).to.have.property('access_token');
});
